		public bool VertifyData(Vertifier dataToVertify)
		{
			if (!this.ClonesEncr.Equals(dataToVertify.ClonesEncr))
			{
				Debug.Log("Clones are invalid! Saved:" + this.Clones + " - new: " + dataToVertify.Clones);
				return false;
			}
			if (!this.TrainingsEncr.Equals(dataToVertify.TrainingsEncr))
			{
				Debug.Log("Trainings are invalid! Saved:" + this.Trainings + " - new: " + dataToVertify.Trainings);
				return false;
			}
			if (!this.CreationsEncr.Equals(dataToVertify.CreationsEncr))
			{
				Debug.Log("Creations are invalid! Saved:" + this.Creations + " - new: " + dataToVertify.Creations);
				return false;
			}
			if (!this.SkillsEncr.Equals(dataToVertify.SkillsEncr))
			{
				Debug.Log("Skills are invalid! Saved:" + this.Skills + " - new: " + dataToVertify.Skills);
				return false;
			}
			if (!this.FightsEncr.Equals(dataToVertify.FightsEncr))
			{
				Debug.Log("Fights are invalid! Saved:" + this.Fights + " - new: " + dataToVertify.Fights);
				return false;
			}
			if (!this.MonumentsEncr.Equals(dataToVertify.MonumentsEncr))
			{
				Debug.Log("Monuments are invalid! Saved:" + this.Monuments + " - new: " + dataToVertify.Monuments);
				return false;
			}
			if (!this.MultiplierEncr.Equals(dataToVertify.MultiplierEncr))
			{
				Debug.Log("Multiplier are invalid! Saved:" + this.Multiplier + " - new: " + dataToVertify.Multiplier);
				return false;
			}
			if (!this.DividerStatisticEncr.Equals(dataToVertify.DividerStatisticEncr))
			{
				Debug.Log("DividerStatistic are invalid! Saved:" + this.DividerStatistic + " - new: " + dataToVertify.DividerStatistic);
				return false;
			}
			if (!this.PremiumEncr.Equals(dataToVertify.PremiumEncr))
			{
				Debug.Log("Premium is invalid! Saved:" + this.Premium + " - new: " + dataToVertify.Premium);
				return false;
			}
			return true;
		}

		public static string Encrypt(string plainText, string password)
		{
			while (password.Length < plainText.Length)
			{
				Debug.Log("password too short!" + plainText);
				password += password;
			}
			byte[] bytes = Encoding.UTF8.GetBytes(plainText);
			byte[] bytes2 = Encoding.UTF8.GetBytes(password);
			int num = 7;
			for (int i = 0; i < bytes.Length; i++)
			{
				if (i % num != 0)
				{
					bytes[i] = (byte)(bytes2[i] ^ bytes[i]);
				}
			}
			return Encoding.UTF8.GetString(bytes);
		}

		public static string Decrypt(string enryptedData, string password)
		{
			while (password.Length < enryptedData.Length)
			{
				Debug.Log("password too short!" + enryptedData);
				password += password;
			}
			byte[] bytes = Encoding.UTF8.GetBytes(enryptedData);
			byte[] bytes2 = Encoding.UTF8.GetBytes(password);
			int num = 7;
			for (int i = 0; i < bytes.Length; i++)
			{
				if (i % num != 0)
				{
					bytes[i] ^= bytes2[i];
				}
			}
			return Encoding.UTF8.GetString(bytes);
		}

		public static string GenerateVertifierFromGameState(GameState state)
		{
			Vertifier vertifier = new Vertifier();
			StringBuilder stringBuilder = new StringBuilder();
			stringBuilder.Append(state.Money.Serialize()).Append(",");
			stringBuilder.Append(state.CreatingPowerBase.Serialize()).Append(",");
			stringBuilder.Append(state.BattlePowerBase.Serialize()).Append(",");
			stringBuilder.Append(state.MysticPowerBase.Serialize()).Append(",");
			stringBuilder.Append(state.PhysicalPowerBase.Serialize()).Append(",");
			stringBuilder.Append(state.Clones.MaxShadowClones.ToString()).Append(",");
			stringBuilder.Append(state.Clones.MaxShadowClonesRebirth.ToString()).Append(",");
			stringBuilder.Append(state.Clones.TotalClonesCreated.Serialize()).Append(",");
			stringBuilder.Append(state.Clones.TotalClonesKilled.Serialize());
			vertifier.ClonesEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			stringBuilder = new StringBuilder();
			foreach (Training current in state.AllTrainings)
			{
				stringBuilder.Append(current.Level).Append(",");
				stringBuilder.Append(current.CurrentDuration);
			}
			vertifier.TrainingsEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			stringBuilder = new StringBuilder();
			foreach (Creation current2 in state.AllCreations)
			{
				if (current2.TypeEnum != Creation.CreationType.Shadow_clone)
				{
					stringBuilder.Append(current2.GodToDefeat.IsDefeated);
					stringBuilder.Append(current2.Count).Append(",");
					stringBuilder.Append(current2.TotalCreated).Append(",");
				}
			}
			vertifier.CreationsEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			stringBuilder = new StringBuilder();
			foreach (Skill current3 in state.AllSkills)
			{
				stringBuilder.Append(current3.Level).Append(",");
				stringBuilder.Append(current3.CurrentDuration).Append(",");
			}
			vertifier.SkillsEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			stringBuilder = new StringBuilder();
			foreach (Fight current4 in state.AllFights)
			{
				stringBuilder.Append(current4.Level).Append(",");
				stringBuilder.Append(current4.CurrentDuration).Append(",");
			}
			vertifier.FightsEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			stringBuilder = new StringBuilder();
			foreach (Monument current5 in state.AllMonuments)
			{
				stringBuilder.Append(current5.Level).Append(",");
				stringBuilder.Append(current5.CurrentDuration).Append(",");
				stringBuilder.Append(current5.Upgrade.Level).Append(",");
			}
			vertifier.MonumentsEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			stringBuilder = new StringBuilder();
			stringBuilder.Append(state.Multiplier.AchievementMultiBattle.Serialize());
			stringBuilder.Append(state.Multiplier.AchievementMultiCreating.Serialize());
			stringBuilder.Append(state.Multiplier.AchievementMultiMystic.Serialize());
			stringBuilder.Append(state.Multiplier.AchievementMultiPhysical.Serialize());
			stringBuilder.Append(state.Multiplier.CurrentMultiBattle.Serialize());
			stringBuilder.Append(state.Multiplier.CurrentMultiCreating.Serialize());
			stringBuilder.Append(state.Multiplier.CurrentMultiMystic.Serialize());
			stringBuilder.Append(state.Multiplier.CurrentMultiPhysical.Serialize());
			stringBuilder.Append(state.Multiplier.MonumentMultiBattle.Serialize());
			stringBuilder.Append(state.Multiplier.MonumentMultiCreating.Serialize());
			stringBuilder.Append(state.Multiplier.MonumentMultiMystic.Serialize());
			stringBuilder.Append(state.Multiplier.MonumentMultiPhysical.Serialize());
			stringBuilder.Append(state.Multiplier.GodMultiFromRebirth.Serialize());
			vertifier.MultiplierEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			stringBuilder = new StringBuilder();
			Conv.AppendValue(stringBuilder, "l", state.CloneAttackDivider);
			Conv.AppendValue(stringBuilder, "m", state.CloneDefenseDivider);
			Conv.AppendValue(stringBuilder, "n", state.CloneHealthDivider);
			stringBuilder.Append(state.Statistic.TotalAchievements);
			stringBuilder.Append(state.Statistic.TotalGodsDefeated);
			stringBuilder.Append(state.Statistic.TotalRebirths);
			stringBuilder.Append(state.IsBuyUnlocked.ToString());
			vertifier.DividerStatisticEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			stringBuilder = new StringBuilder();
			stringBuilder.Append(state.PremiumBoni.TotalItemsBought);
			stringBuilder.Append(state.PremiumBoni.GodlyLiquidCount);
			stringBuilder.Append(state.PremiumBoni.ChakraPillCount);
			stringBuilder.Append(state.PremiumBoni.UltimateShadowSummonCount);
			stringBuilder.Append(state.PremiumBoni.GodlyLiquidDuration);
			stringBuilder.Append(state.PremiumBoni.ChakraPillDuration);
			vertifier.PremiumEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			stringBuilder = new StringBuilder();
			if (Kongregate.APILoaded && !Kongregate.IsGuest)
			{
				stringBuilder.Append(Kongregate.UserId + Kongregate.GameToken);
			}
			else
			{
				stringBuilder.Append("Guest");
			}
			vertifier.UseridEncr = Vertifier.Encrypt(stringBuilder.ToString(), "");
			return vertifier.Serialize();
		}

		public string Serialize()
		{
			StringBuilder stringBuilder = new StringBuilder();
			Conv.AppendValue(stringBuilder, "a", this.ClonesEncr);
			Conv.AppendValue(stringBuilder, "b", this.TrainingsEncr);
			Conv.AppendValue(stringBuilder, "c", this.CreationsEncr);
			Conv.AppendValue(stringBuilder, "d", this.SkillsEncr);
			Conv.AppendValue(stringBuilder, "e", this.FightsEncr);
			Conv.AppendValue(stringBuilder, "f", this.MonumentsEncr);
			Conv.AppendValue(stringBuilder, "g", this.MultiplierEncr);
			Conv.AppendValue(stringBuilder, "h", this.DividerStatisticEncr);
			Conv.AppendValue(stringBuilder, "i", this.PremiumEncr);
			Conv.AppendValue(stringBuilder, "j", this.UseridEncr);
			return Conv.ToBase64(stringBuilder.ToString(), "VertifierNames");
		}

		internal static Vertifier FromString(string base64String)
		{
			if (string.IsNullOrEmpty(base64String))
			{
				Debug.Log("VertifierNames.FromString with empty value!");
				return new Vertifier();
			}
			string[] parts = Conv.StringPartsFromBase64(base64String, "VertifierNames");
			return new Vertifier
			{
				ClonesEncr = Conv.getStringFromParts(parts, "a"),
				TrainingsEncr = Conv.getStringFromParts(parts, "b"),
				CreationsEncr = Conv.getStringFromParts(parts, "c"),
				SkillsEncr = Conv.getStringFromParts(parts, "d"),
				FightsEncr = Conv.getStringFromParts(parts, "e"),
				MonumentsEncr = Conv.getStringFromParts(parts, "f"),
				MultiplierEncr = Conv.getStringFromParts(parts, "g"),
				DividerStatisticEncr = Conv.getStringFromParts(parts, "h"),
				PremiumEncr = Conv.getStringFromParts(parts, "i"),
				UseridEncr = Conv.getStringFromParts(parts, "j")
			};
		}
